  border: 1px solid var(--dse-form-border-color, #f1f2f3);
  background: var(--dse-form-bg-color, #fff);
  font-family: var(--dse-body-font-family, "Playfair Display", serif);
  cursor: pointer;
}

.dse-select__overlay {
  padding: 0rem;
  margin: 0rem;
