  box-shadow: 0 4px 11px rgba(157, 157, 157, 0.21);
  position: absolute;
  width: 100%;
}

.dse-select__option {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  box-sizing: border-box;
  cursor: pointer;
  padding: 0.75rem;
}

.dse-select__option:hover {
  background-color: var(--dse-form-bg-color-hover, #f1f2f3);
}

.dse-select__option--selected, .dse-select__option--selected:hover {
  background-color: var(--dse-form-bg-option-selected, #5ece7b);
  color: var(--dse-form-color-option-selected, #fff);
}

.dse-select__caret {
  transition: all 250ms ease;
}

.dse-select__caret--open {
  transform: rotate(180deg);
}

.dse-select__caret--close {
  transform: rotate(0deg);
}

